      var formalsString = '[' + formals.map(JSON.stringify).join(', ') + ']';
      sb.append('(' + JSON.stringify(ruleName) + ', ' + formalsString + ', ');
      body.outputRecipe(sb, formals);
      if (!self.superGrammar.ruleBodies[ruleName] && self.ruleDescriptions[ruleName]) {
